import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-missing-number',
  templateUrl: './missing-number.component.html',
  styleUrls: ['./missing-number.component.css']
})
export class MissingNumberComponent implements OnInit {
  inputvalue: number = 0;
  result: number = 0;

  ngOnInit(): void {
  }

  constructor() { }
  resultnumber(inputvalue: number) {
    if (inputvalue % 2 === 0) {
      this.result = inputvalue * inputvalue - 1;
    } else {
      this.result = inputvalue * inputvalue + 1;
    }
  }


}
<!DOCTYPE html>
<html>
<head>
  <title>Awesome Solutions</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      background-color: #f7f7f7;
      margin: 0;
      padding: 0;
    }

    .container {
      max-width: 800px;
      margin: 0 auto;
      padding: 20px;
      background: linear-gradient(135deg, #64B5F6, #FFD54F); /*  gradient colors */
      border-radius: 10px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
      color: #fff; /*  text color */
    }

    .center {
      text-align: center;
      margin-top: 30px;
    }

    .border {
      border: 1px solid #fff; /*  border color */
      padding: 10px;
      border-radius: 5px;
    }

    .border span {
      display: inline-block;
      margin-top: 5px;
      color: #fff; /* same text color as the container background */
    }

    .border input {
      padding: 5px;
      border-radius: 3px;
      border: 1px solid #ccc; /*  input border color */
      color: #000; /*  text color for input */
    }

    .btn {
      background-color: #007bff; /*  button color */
      color: #fff; /*  button text color */
      padding: 5px 10px;
      border: none;
      border-radius: 3px;
      cursor: pointer;
    }

    .btn:hover {
      background-color: #0056b3; /*  button hover color */
    }
  </style>
</head>
<body>
  <div class="container">
    <div class="row center"><b>Solution 1</b></div>
    <br>
    <br>
    <div class="row mt-2 border">
      <span class="mt-1" style="color: #fff;">Please enter the position of the number</span>
      <span class="mt-1 ml-2"
        ><input type="number" [(ngModel)]="inputvalue"
          style="color: #000;" 
      /></span>
      <span class="mt-1 ml-2"
        ><button class="btn btn-primary" (click)="resultnumber(inputvalue)">
          Result
        </button></span
      >
      <span class="mt-1 ml-2" style="color: #fff;">Result is : {{ result }}</span>
    </div>
  </div>
</body>
</html>
